import logo from './logo.svg';
import React, { useMemo, useState } from 'react'
import './App.css';

function App() {

  const [counter, setCounter] = useState(0)
  const [counter2, setCounter2] = useState(0)

  const isEven = useMemo(() => {
    let i = 0;
    while (i < 900000000) {
      i++
    }
    return counter % 2 == 0
  }, [counter])
  console.log(counter);
  return (
    <div className="App">
      {isEven ? 'even' : 'odd'}
      <button onClick={(prev) => setCounter(counter + 1)}>btn1</button>
      <button onClick={(prev) => setCounter2(counter2 + 1)}>btn2</button>
    </div>
  );
}

export default App;
