import logo from './logo.svg';
import React, { useState } from 'react'
import './App.css';

function App() {

  const [counter, setCounter] = useState(0)
  const [counter2, setCounter2] = useState(0)

  const isEven = () => {
    let i = 0;
    // while (i 900) {
    //   i++
    // }
    return counter % 2 == 0
  }

  return (
    <div className="App">
      {isEven() ? 'even' : 'odd'}
      <button onClick={(prev) => setCounter(prev + 1)}>btn1</button>
      <button onClick={(prev) => setCounter2(prev + 1)}>btn2</button>
    </div>
  );
}

export default App;
