import { createSlice } from "@reduxjs/toolkit";

export const count = createSlice({
    name: '',
    initialState: {
        value: 0
    },
    reducer: {
        increament: (state) => {
            state.value += 1
        },
        decreament: (state) => {
            state.value -= 1
        }
    }
})
