import { createAsyncThunk, createSlice } from "@reduxjs/toolkit";

export const fetchTodos = createAsyncThunk("todo/fetchTodosStatus", async () => {
    const res = await fetch("https://todo-backend-rest.onrender.com/todos");
    const todos = await res.json();
    return todos;
});

export const count = createSlice({
    name: 'counter',
    initialState: {
        value: 0
    },
    reducers: {
        increament: (state) => {
            state.value += 1
        },
        decreament: (state) => {
            state.value -= 1
        },
        [fetchTodos.pending]: (state, _) => {
            state.loading = true;
            state.error = null;
        },
        [fetchTodos.fulfilled]: (state, action) => {
            state.todos = action.payload;
            state.loading = false;
        },
        [fetchTodos.rejected]: (state, action) => {
            state.loading = false;
            state.error = action.payload;
        }
    }
})


export const { increament, decreament } = count.actions

export default count.reducer
