import { createSlice } from "@reduxjs/toolkit";

export const count = createSlice({
    name: 'counter',
    initialState: {
        value: 0
    },
    reducers: {
        increament: (state) => {
            state.value += 1
        },
        decreament: (state) => {
            state.value -= 1
        }
    }
})

export const { increment, decrement } = count.actions

export default count.reducer
